angular.module("app.movie",["ngRoute","imdb.service"]),angular.module("app.movie").config(["$routeProvider",function($routeProvider){$routeProvider.when("/",{templateUrl:"/templates/list.html",controller:"ListController"}).when("/list",{templateUrl:"/templates/list.html",controller:"ListController"}).when("/gallery",{templateUrl:"/templates/gallery.html"}).when("/details/:movieId",{templateUrl:"/templates/details.html",controller:"DetailsController"}).otherwise({redirectTo:"/"})}]);